import React, { useState } from 'react';
import Header from './Component/Header';
import Table from './Component/Table';
import UserForm from './Component/UserForm';

function App() {
  // const [yearlyDataSet, setYearlyData] = useState('');
  const [data, setData] = useState({
    'current-savings': 0,
    'yearly-contribution': 0,
    'expected-return': 0,
    duration: 0,
  });
  const calculateHandler = (userInput) => {
    // userInput.preventDefault();
    console.log(userInput, 'userInput');
    // // Should be triggered when form is submitted
    // // You might not directly want to bind it to the submit event on the form though...
    // setData(prevdata => {
    //   return { ...prevdata, ...data }
    // })
    // per-year results
    const yearlyData = [];
    let currentSavings = +userInput['current-savings']; // feel free to change the shape of this input object!
    const yearlyContribution = +userInput['yearly-contribution']; // as mentioned: feel free to change the shape...
    const expectedReturn = +userInput['expected-return'] / 100;
    const duration = +userInput['duration'];

    console.log(yearlyData, 'd', currentSavings, 'e', yearlyContribution, 'f', expectedReturn, 'g', duration);

    // The below code calculates yearly results (total savings, interest etc)
    for (let i = 0; i < duration; i++) {
      const yearlyInterest = currentSavings * expectedReturn;
      currentSavings += yearlyInterest + yearlyContribution;
      yearlyData.push({
        // feel free to change the shape of the data pushed to the array!
        year: i + 1,
        yearlyInterest: yearlyInterest,
        savingsEndOfYear: currentSavings,
        yearlyContribution: yearlyContribution,
      });
    }
    console.log(yearlyData, 'yearly');
    // setData(prevdata => {
    //   prevdata.push(yearlyData)
    // })
    console.log(data, 'data');
    // do something with yearlyData ...
  };

  return (
    <div>
      <Header />
      <UserForm  {...{ calculateHandler }} />
      {/* Todo: Show below table conditionally (only once result data is available) */}
      {/* Show fallback text if no data is available */}
      {/* <Table data={data} /> */}
    </div>
  );
}

export default App;
