import React from "react";
import InputField from './InputField';

const UserForm = ({ calculateHandler, data }) => {
    const sumbitFormHandler = (event) => {
        event.preventDefault();
        console.log('submit');
    }
    const resetFormHandler = () => {
        console.log('reset');
    }
    const changeHandler = (event) => {

    }
    return (
        <form className="form" onSubmit={sumbitFormHandler}>
            <div className="input-group">
                <InputField htmlFor="current-savings" labelText='Current Savings ($)' type="number" id="current-savings" value={data['current-savings']} onChange={changeHandler} />
                <InputField htmlFor="yearly-contribution" labelText='Yearly Savings ($)' value={data['yearly-contribution']} type="number" id="yearly-contribution" onChange={changeHandler} />
            </div>
            <div className="input-group">
                <InputField htmlFor="expected-return" labelText='Expected Interest (%, per year)' type="number" id="expected-return" value={data['expected-return']} onChange={changeHandler} />
                <InputField htmlFor="duration" labelText='Investment Duration (years)' type="number" id="duration" value={data['duration']} onChange={changeHandler} />
            </div>
            <p className="actions">
                <button onClick={resetFormHandler} type="reset" className="buttonAlt">
                    Reset
                </button>
                <button type="submit" className="button">
                    Calculate
                </button>
            </p>
        </form>
    )
}
export default UserForm;
