import React, { useState } from "react";
import InputField from './InputField';

const UserForm = ({ calculateHandler, data }) => {
    const [inputValue, setInputValue] = useState({
        'current-savings': 0,
        'yearly-contribution': 0,
        'expected-return': 0,
        duration: 0,
    });
    const sumbitFormHandler = (event) => {
        event.preventDefault();
        console.log('submit');
    }
    const resetFormHandler = () => {
        console.log('reset');
    }

    return (
        <form className="form" onSubmit={sumbitFormHandler}>
            <div className="input-group">
                <InputField htmlFor="current-savings" labelText='Current Savings ($)' type="number" id="current-savings" />
                <InputField htmlFor="yearly-contribution" labelText='Yearly Savings ($)' type="number" id="yearly-contribution" />
            </div>
            <div className="input-group">
                <InputField htmlFor="expected-return" labelText='Expected Interest (%, per year)' type="number" id="expected-return" />
                <InputField htmlFor="duration" labelText='Investment Duration (years)' type="number" id="duration" />
            </div>
            <p className="actions">
                <button onClick={resetFormHandler} type="reset" className="buttonAlt">
                    Reset
                </button>
                <button type="submit" className="button">
                    Calculate
                </button>
            </p>
        </form>
    )
}
export default UserForm;
